mapper.insert:

INSERT INTO saleImage
(
    serial,
    saleImg
)
VALUES
(
    (SELECT serial
    FROM sale
    WHERE registerDate = #{registerDate}
    AND title = #{title}
    LIMIT 1),
    #{imagePath}
)


INSERT INTO requestImage
(
    serial,
    requestImg
)
VALUES
(
    (SELECT serial
    FROM request
    WHERE registerDate = #{registerDate}
    AND title = #{title}
    LIMIT 1),
    #{imagePath}
)
